let A = 0.001125308852122;
let B = 0.000234711863267;
let C = 0.000000085663516;

let R = msg.payload * 1000;

// In Kelvin
let Trecip = A + B * Math.log(R) + C * (Math.log(R)) ** 3;
let T = 1 / Trecip;

// In Fahrenheit
let f = (T - 273.15) * 9.0 / 5.0 + 32.0;
msg.payload = f.toFixed(1);
return msg;

let f = msg.payload;
let c = (f - 32.0) * 5 / 9;

msg.payload = c.toFixed(1);
return msg;

/* Returns an array of 6 booleans with one of [1..4] elements set to true, indicating
   the pump's current speed: Speed1, Speed2, Speed3 or Speed0 (standby current).
   The element [0] is set to true if the pump current is higher by a resonable margin
   than Speed1. The element [5] is set to true if the pump current is close to 0,
   indicating a tripped circuit breaker.
*/
var current = msg.payload;
global.set("vspCurrent", current);
if(current >= 2.3) {
    global.set("vspSpeed", "Speed1");
    return [{payload:true}, {payload:true}, {payload:false}, {payload:false}, {payload:false}, {payload:false}];
} else if(current >= 1.8) {
    global.set("vspSpeed", "Speed1");
    return [{payload:false}, {payload:true}, {payload:false}, {payload:false}, {payload:false}, {payload:false}];
} else if(current >= 1.1) {
    global.set("vspSpeed", "Speed2");
    return [{payload:false}, {payload:false}, {payload:true}, {payload:false}, {payload:false}, {payload:false}];
} else if(current >= 0.5) {
    global.set("vspSpeed", "Speed3");
    return [{payload:false}, {payload:false}, {payload:false}, {payload:true}, {payload:false}, {payload:false}];
} else if(current >= 0.05) {
    global.set("vspSpeed", "Speed0");
    return [{payload:false}, {payload:false}, {payload:false}, {payload:false}, {payload:true}, {payload:false}];
} else {
    global.set("vspSpeed", "Speed0");
    return [{payload:false}, {payload:false}, {payload:false}, {payload:false}, {payload:true}, {payload:true}];
}

var temp = msg.payload;
var FanStartTemp = global.get("FanStartTemp");
var FanStopTemp = global.get("FanStopTemp");

if (temp > FanStartTemp) {
    msg.payload = true;
}
else if (temp < FanStopTemp) {
    msg.payload = false;
}
return msg;

var temp = msg.payload;
global.set("FanStartTemp", temp);
return msg;

var temp = msg.payload;
global.set("FanStopTemp", temp);
return msg;

let timestamp = msg.payload;
let time = new Date(timestamp).toLocaleTimeString();
return {
    payload: time
}

let expr = msg.payload;
// vcgencmd measure_temp prints to standard output"
// temp=54.5'C
msg.payload = expr.substring(5, expr.lastIndexOf("'"));
return msg;

let alarmSubject = "High VSP current alarm";
let alarmContents = "VSP current is " + global.get("vspCurrent") + " A";
let alarmClearedSubject = "High VSP current alarm cleared";
let alarmClearedContents = "No contents";
if(msg.payload == true) {
    msg.topic = alarmSubject;
    msg.payload = alarmContents;
} else {
    msg.topic = alarmClearedSubject;
    msg.payload = alarmClearedContents;
}
return msg;

let alarmSubject = "Low VSP current alarm";
let alarmContents = "VSP current is " + global.get("vspCurrent") + " A";
let alarmClearedSubject = "Low VSP current alarm cleared";
let alarmClearedContents = "No contents";
if (msg.payload == true) {
    msg.topic = alarmSubject;
    msg.payload = alarmContents;
} else {
    msg.topic = alarmClearedSubject;
    msg.payload = alarmClearedContents;
}
return msg;

var SWCGSwitch = msg.payload;
var vspSpeed = global.get("vspSpeed");
if (SWCGSwitch == false || SWCGSwitch == true && vspSpeed != "Speed0") {
    // Pass the message to the SWCG relay.
    return [msg, null];
} else {
    // VSP speed is not appropriate for the SWCG to run.
    return [null, { payload: "For SWCG to run, the filter pump must be on" }];
}

var isSpeed0 = msg.payload;
if (isSpeed0 == true) {
    return { payload: false };
}

var vspSpeed = global.get("vspSpeed");
var swcgSwitch = msg.payload;
if (vspSpeed == "Speed0" && swcgSwitch == true) {
     return { payload: false };
}

var polarisSwitch = msg.payload;
var vspSpeed = global.get("vspSpeed");
if(polarisSwitch == false || polarisSwitch == true && vspSpeed == "Speed2") {
    // Pass the message to the Polaris relay.
    return [msg, null];
} else {
    // VSP speed is not appropriate for the Polaris pump to run.
    return [null, {payload: "For Polaris to run, filter pump must run at speed 2"}];
}

var vspSpeed = global.get("vspSpeed");
var polarisSwitch = msg.payload;
if (vspSpeed != "Speed2" && polarisSwitch == true) {
     return { payload: false };
}

var isSpeed2 = msg.payload;
if (isSpeed2 == false) {
     return { payload: false };
}

var current = msg.payload;
msg.topic = 'Duty Cycle';
if (current > 0.035) {
    if (current > 0.35)
        msg.payload = 100.0;
    else
        msg.payload = 0.0;
}
else {
    // Don't send any message to the next node in the flow
    msg = null;
}
return msg;
